import React, { useState } from 'react';
import { useSelector, useDispatch} from 'react-redux'
import { removeTodo, addNewTodo } from '../actions/ActionCreator'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from 'react-router-dom'
import {TodoList} from './TodoList'
import {TodoCreate} from './TodoCreate'
import {TodoDetail} from './TodoDetail'

type State = { // StoreStateで保持しているデータの型を定義する！ => データ構造が明確化される(見える化)！
  StoreState:{
    todolist: [id:number, todo:string, handler:string]
  }
}

const todoSelector = (state: State)=> {
  return state.StoreState.todolist
}

const App: React.FC = ()=> { // React.FCは関数コンポーネントであることを型定義している。

  const dispatch = useDispatch()

  const todoData = useSelector(todoSelector)

  const remove = (index: number)=> {
    console.log('remove')
    dispatch(removeTodo(index))
  }

  const [inputString, setInput] = useState<string>('') 

  const inputTodo = (e:any)=>{
    console.log(e.target.value)
    setInput(e.target.value)
  }

  const addTodo = ()=> {
    if(inputString === ''){
      return 
    } else {
      dispatch(addNewTodo(inputString))
      setInput('')
    }
  }

  return (
    <React.Fragment>
      <Router>
        <h1>React-TypeScriptで作るタスク管理ツール</h1>

        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>チケット</th>
              <th>担当者</th>
            </tr>
          </thead>
          <tbody>
            {
              todoData.map(
                (todo: any, index: number)=>{
                  return (
                    <tr key={todo.id}>
                      <td>{todo.id}</td>
                      <td>{todo.todo}</td>
                      <td>{todo.handler}</td>
                      <td><button onClick={ ()=>{remove(index)} }>削除</button></td>
                    </tr>
                  )
                }
              )
            }
          </tbody>
        </table>

        <div>
          <h3>Todo作成</h3>
          <input value={inputString} onChange={ (e)=> {inputTodo(e)} } />
          <button onClick={ ()=> {addTodo()} } >作成</button>
        </div>

        <Switch>
          <Route  />
        </Switch>

      </Router>
    </React.Fragment>
  )
}

export default App;
