export const ADDNEWTODO = 'addNewTodo'
export const REMOVETODO = 'removeTodo'

interface Todo {
    id: number,
    todo: string,
    handler: string
  }

export const addNewTodo = (todo: string)=> {
    return {
        type:ADDNEWTODO,
        todo:todo
    }
}

export const removeTodo = (index: number)=> {
    return {
        type:REMOVETODO,
        index:index
    }
}
