import {ADDNEWTODO,REMOVETODO} from '../actions/ActionCreator'

export const initialState = {
    todolist:[ 
      {id:1,todo:'Reactの学習',handler:'ロボ玉試作1号機'},
      {id:2,todo:'TypeScriptの学習',handler:'ロボ玉試作2号機'},
      {id:3,todo:'Nodeの学習',handler:'ロボ玉試作3号機'}
    ]
  }

// eslint-disable-next-line import/no-anonymous-default-export
export default (state = initialState, action: any)=> {

    switch(action.type){
        case ADDNEWTODO: {
            const copyTodo = state.todolist.slice()
            copyTodo.push( {id:copyTodo.length+1,todo:action.inputtodo,handler:'ロボ玉'} )
            return { ...state, todolist:copyTodo }
        }
        case REMOVETODO: {
            const copyTodo = state.todolist.slice() // コピー
            copyTodo.splice(action.index,1)

            return { ...state, todolist:copyTodo }
        }
        default: return state
    }
}
