import React, { useState } from 'react';
import { useSelector, useDispatch} from 'react-redux'
import { removeTodo } from '../actions/ActionCreator'

type State = { // StoreStateで保持しているデータの型を定義する！ => データ構造が明確化される(見える化)！
  StoreState:{
    todolist: [id:number, todo:string, handler:string]
  }
}

const todoSelector = (state: State)=> {
  return state.StoreState.todolist
}

const App: React.FC = ()=> { // React.FCは関数コンポーネントであることを型定義している。

  const dispatch = useDispatch()

  const todoData = useSelector(todoSelector)

  const remove = (index: number)=> {
    console.log('remove')
    dispatch(removeTodo(index))
  }

  const [inputString, setInput] = useState<string>('') 

  const InputTodo = (e:any)=>{
    console.log(e.target.value)
    setInput(e.targe.value)
  }



  return (
    <React.Fragment>
      <h1>React-TypeScriptで作るタスク管理ツール</h1>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>チケット</th>
            <th>担当者</th>
          </tr>
        </thead>
        <tbody>
          {
            todoData.map(
              (todo: any, index: number)=>{
                return (
                  <tr key={todo.id}>
                    <td>{todo.id}</td>
                    <td>{todo.todo}</td>
                    <td>{todo.handler}</td>
                    <td><button onClick={ ()=>{remove(index)} }>削除</button></td>
                  </tr>
                )
              }
            )
          }
        </tbody>
      </table>

      <div>
        <h3>Todo作成</h3>
        <input onChange={ (e)=> {InputTodo(e)} } />
        <button>作成</button>
      </div>


    </React.Fragment>
  )
}

export default App;
