export const ADDTODO = 'addTodo'
export const REMOVETODO = 'removeTodo'

interface Todo {
    id: number,
    todo: string,
    handler: string
  }

export const addTodo = (todo: Todo)=> {
    return {
        type:ADDTODO,
        todo:todo
    }
}

export const removeTodo = (index: number)=> {
    return {
        type:REMOVETODO,
        index:index
    }
}
