import React from 'react';
import removeTodo from '../actions/ActionCreator'

const App = ()=> {

  const initialState = {
    todolist:[ 
      {id:1,todo:'Reactの学習',handler:'ロボ玉試作1号機'},
      {id:2,todo:'Vueの学習',handler:'ロボ玉試作2号機'},
      {id:3,todo:'FireStoreの学習',handler:'ロボ玉試作3号機'}
    ]
  }

  interface Todo {
    id: number,
    todo: string,
    handler: string
  }

  // const remove = (index)=> {

  //   dispatch(removeTodo(index))
  // }

  return (
    <React.Fragment>
      <h1>React-TypeScriptで作るタスク管理ツール</h1>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>チケット</th>
            <th>担当者</th>
          </tr>
        </thead>
        <tbody>
          {
            initialState.todolist.map(
              (todo: Todo, index: number)=>{
                return (
                  <tr key={todo.id}>
                    <td>{todo.id}</td>
                    <td>{todo.todo}</td>
                    <td>{todo.handler}</td>
                    <td><button>削除</button></td>
                  </tr>
                )
              }
            )
          }
        </tbody>
      </table>

    </React.Fragment>
  )
}

export default App;
