import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { addNewTodo } from '../actions/ActionCreator'

export const TodoCreate: React.FC = ()=> {
    const dispatch = useDispatch()

    const [inputtodo,setTodo] = useState('') 
    const [inputdetail,setTodoDetail] = useState('')
    const [inputhandler,setHandler] = useState('')

    const inputTodo = (event: any)=>{ 
        setTodo(event.target.value) // チケット名
    }

    const inputDetail = (event: any)=>{
        setTodoDetail(event.target.value) // チケット詳細
    }

    const inputHandler = (event)=>{
        setHandler(event.target.value) // 担当者
    }

     // stateの値を直で変更することはしてはいけません。なので、一度他の変数などで受け取る必要がある。
     const submitTask = (inputtodo)=>{
        if(inputtodo === ''){return}

        console.log('dispatch!addTodo')
        dispatch(addNewTodo(inputtodo)) // ActionCreatorの関数addTodoにデータを送る。
        setTodo('') // setTask呼び出し・初期化

    }

    return (
        <React.Fragment>
            <h2>Todo作成画面</h2>

            <table>
                    <tr>
                        <td>
                            <h2>
                                チケット名： <input value={inputtodo} placeholder='チケットのタイトル' onChange={(event)=>{inputTodo(event)}} />
                            </h2>
                        </td>
                    </tr>
                    <tr>
                        <td>
                            <h2>
                                チケット詳細： <textarea value={inputdetail} placeholder='タスクの詳細' onChange={(event)=>{inputDetail(event)}} />
                            </h2>
                        </td>
                    </tr>
                    <tr>
                        <td>
                            <h2>
                                担当者： <input value={inputhandler} placeholder='担当者を入力' onChange={(event)=>{inputHandler(event)}} />
                            </h2>
                        </td>
                    </tr>
                    <tr>
                        <td>
                            <h2>
                                期日： <input value={inputtodo} onChange={(event)=>{inputTodo(event)}} />
                            </h2>
                        </td>
                    </tr>
                    <tr>
                        <td>
                            <h2>
                                開始日： <input value={inputtodo} onChange={(event)=>{inputTodo(event)}} />
                            </h2>
                        </td>
                    </tr>
                    <tr>
                        <td><button onClick={ ()=>{submitTask(inputtodo)} }>Todoチケット作成</button></td>
                    </tr>
                </table>




        </React.Fragment>
    )
}

// ・チケット作成画面
// 　・チケット名、詳細（内容）、担当者、期日、開始日　を入力して保存ができる
// 　・保存ボタン押下後は一覧画面に遷移

// チケット作成画面　　　条件詳細
// -チケット名　← 1文字以上20文字以内　
// -詳細（内容）　←1文字以上300文字以内
// -担当者　1文字以上10文字以内
// -期日、開始日　←yyyy/mm/dd の記載方法でなければならない
