export const ADDNEWTODO = 'addNewTodo'
export const REMOVETODO = 'removeTodo'

export const addNewTodo = (inputtodo: string)=> {
    return {
        type:ADDNEWTODO,
        todo:inputtodo
    }
}

export const removeTodo = (index: number)=> {
    return {
        type:REMOVETODO,
        index:index
    }
}
