import React from 'react'

export const TodoList: React.FC = ()=> {

    const dispatch = useDispatch()

  const todoData = useSelector(todoSelector)

  const remove = (index: number)=> {
    console.log('remove')
    dispatch(removeTodo(index))
  }

  const [inputString, setInput] = useState<string>('') 

  const inputTodo = (e:any)=>{
    console.log(e.target.value)
    setInput(e.target.value)
  }

  const addTodo = ()=> {
    if(inputString === ''){
      return 
    } else {
      dispatch(addNewTodo(inputString))
      setInput('')
    }
  }

    return (
        <React.Fragment>
        <h1>Todo一覧画面</h1>

        <h1>React-TypeScriptで作るタスク管理ツール</h1>

        <table>
        <thead>
            <tr>
            <th>ID</th>
            <th>チケット</th>
            <th>担当者</th>
            </tr>
        </thead>
        <tbody>
            {
            todoData.map(
                (todo: any, index: number)=>{
                return (
                    <tr key={todo.id}>
                    <td>{todo.id}</td>
                    <td>{todo.todo}</td>
                    <td>{todo.handler}</td>
                    <td><button onClick={ ()=>{remove(index)} }>削除</button></td>
                    </tr>
                )
                }
            )
            }
        </tbody>
        </table>

        <div>
        <h3>Todo作成</h3>
        <input value={inputString} onChange={ (e)=> {inputTodo(e)} } />
        <button onClick={ ()=> {addTodo()} } >作成</button>
        </div>
        </React.Fragment>
    )
}
