import React from 'react'
import { useParams, useHistory } from 'react-router-dom'
import { useSelector, useDispatch } from "react-redux"
//import { State } from './TodoList'

type State = { // StoreStateで保持しているデータの型を定義する！ => データ構造が明確化される(見える化)！
    StoreState:{
      todolist: [
           { id: number, todo: string, handler: string, date: string, start: string }
        ]
    }
}

// type TodoList = [
//     { id: number, todo: string, handler: string, date: string, start: string }
// ]

const todoSelector = (state: State)=> {
    return state.StoreState.todolist
}

export const TodoDetail: React.FC = ()=> {

    const todoData = useSelector(todoSelector)

    const history = useHistory()
    const handleLink = (path: string)=> { return history.push(path)}

    type Parameter = { id: string}

    const {id}: Parameter = useParams()
    console.log({id})
    console.log(id)
    //console.log(useParams()) // useParamsの正体がわかる
    // console.log(paramsId) // useParams()を格納しているid
    // console.log(paramsId.id)

    type detail = { id: number, todo: string, handler: string, date: string, start: string }

    const detailTodo: detail | undefined = todoData.find( (todo: detail): boolean => todo.id === Number(id))
    console.log(detailTodo)

     // チケット名、詳細（内容）、担当者、期日、開始日
    return (
        <React.Fragment>
            <h2>Todo詳細画面</h2>
            <table>
                <tbody>
                    <tr>
                        <th>
                            <h3>Todo名</h3>
                        </th>
                        <td>
                            <h5>{detailTodo.todo}</h5>
                        </td>
                    </tr>
                    <tr>
                        <th>
                            <h3>チケット詳細</h3>
                        </th>
                        <td>
                            <h5>ロボ玉</h5>
                        </td>
                    </tr>
                    <tr>
                        <th>
                            <h3>担当者</h3>
                        </th>
                        <td>
                            <h5>ロボ玉</h5>
                        </td>
                    </tr>
                    <tr>
                        <th>
                            <h3>期日</h3>
                        </th>
                        <td>
                            <h5>ロボ玉</h5>
                        </td>
                    </tr>
                    <tr>
                        <th>
                            <h3>開始日</h3>
                        </th>
                        <td>
                            <h5>ロボ玉</h5>
                        </td>
                    </tr>
                </tbody>
            </table>
        </React.Fragment>
    )
}


